mapper.select:
SELECT

FROM provider_count
WHERE shop_id = #{shopId,jdbcType=VARCHAR} and work_time = #{workTime,jdbcType=TIMESTAMP}

mapper.update:
UPDATE provider_count
SET consume_tiime = (SELECT sum(consume_time) from customer_appointment where shop_id = #{shopId} and work_time = #{workTime})
where shop_id = #{shopId} and work_time = #{workTime}
